#include "PathDataPanel.h"

PathDataPanel::PathDataPanel()
{
    paths.add (getUSBIcon());
    paths.add (getBluetoothIcon());
    paths.add (getSpeakerIcon());
    paths.add (getPhoneIcon());
    paths.add (getTwitterIcon());
    paths.add (getFacebookIcon());
}

void PathDataPanel::paint (Graphics& g)
{
    g.fillAll (Colours::white);
    
    auto innerBounds = getLocalBounds().toFloat().reduced (20.0f, 20.0f);
    auto itemBounds  = innerBounds.withWidth (innerBounds.getWidth() / paths.size()).withHeight (innerBounds.getHeight() / 2);
    
    drawPaths (g, itemBounds, false);
    
    itemBounds.setPosition (innerBounds.getX(), innerBounds.getY() + itemBounds.getHeight());
    
    drawPaths (g, itemBounds, true);
}

void PathDataPanel::drawPaths (Graphics& g, Rectangle<float> itemBounds, const bool stroke)
{
    for (auto& path : paths)
    {
        drawPath (g, itemBounds.reduced (5.0f, 5.0f), path, stroke);
        itemBounds.translate (itemBounds.getWidth(), 0.0f);
    }
}

void PathDataPanel::drawPath (Graphics& g, Rectangle<float> area, Path path, const bool stroke)
{
    path.applyTransform (path.getTransformToScaleToFit (area.reduced (8.0f, 8.0f), true));
    if (stroke)
        g.strokePath (path, PathStrokeType (1.0f));
    else
        g.fillPath (path);
}

